	def calc_J(self,X_vals,y_vals):
		""" calc_J
		"""
		X=self.X
		y=self.y
		J = self.J_Theta
		calculated_J = theano.function([], 
							outputs = J,
							givens = { X : X_vals.astype(theano.config.floatX),
										y : y_vals.astype(theano.config.floatX) } )
		return calculated_J()


	
